create table tb_caracteristica (caracteristica_id bigint generated by default as identity, icone varchar(255), nome varchar(255), primary key (caracteristica_id));
create table tb_categoria (categoria_id bigint generated by default as identity, descricao varchar(255), nome varchar(255), qualificacao varchar(255), url_imagem varchar(255), primary key (categoria_id));
create table tb_cidade (cidade_tb bigint generated by default as identity, nome varchar(255), pais varchar(255), primary key (cidade_tb));
create table tb_cliente (usuario_id bigint not null, data_validacao_registro timestamp, email varchar(255), nome varchar(255), senha varchar(255), sobrenome varchar(255), usuario_validado boolean, funcao_id bigint, endereco varchar(255), primary key (usuario_id));
create table tb_data_reserva_qtd (data date not null, quantidade_reservas integer, produto_id bigint not null, primary key (data, produto_id));
create table tb_funcao (funcao_id bigint generated by default as identity, nome_funcao_enum varchar(255), primary key (funcao_id));
create table tb_imagem (imagem_id bigint generated by default as identity, titulo varchar(255), url varchar(255), produto_id bigint, primary key (imagem_id));
create table tb_produto (produto_id bigint generated by default as identity, descricao varchar(255), limite_pessoas_por_dia integer, nome varchar(255), categoria_id bigint, cidade_id bigint, primary key (produto_id));
create table tb_reserva (reserva_id bigint generated by default as identity, data_final date, data_inicio date, horario_inicio time, qtd_pessoas integer, usuario_id bigint, produto_id bigint, primary key (reserva_id));
create table tb_usuario (usuario_id bigint not null, data_validacao_registro timestamp, email varchar(255), nome varchar(255), senha varchar(255), sobrenome varchar(255), usuario_validado boolean, funcao_id bigint, primary key (usuario_id));
